jive.namespace("Tips");jive.Tips.TipGroupState=jive.oo.Class.extend(function(a){a.init=function(b){this.stateKey=b.stateKey;this.currentTipId=b.currentTipID||null;this.dismissed=b.dismissed||false;this.skipped=false};this.setCurrentTipId=function(b){this.currentTipId=b};this.getCurrentTipId=function(){return this.currentTipId};this.setDismissed=function(b){this.dismissed=b};this.isDismissed=function(){return this.dismissed};this.setSkipped=function(b){this.skipped=b};this.isSkipped=function(){return this.skipped};this.getKey=function(){return this.stateKey};this.serialize=function(){var b=this.isDismissed()?"true":"false";if(this.currentTipId){b+="#"+this.currentTipId}return b}});