jive.namespace("Tips");jive.Tips.PageView=jive.AbstractView.extend(function(a){a.init=function(c){var b=this;b.currentTipGroupIndex=0;b.tipGroups=c.tipGroups||[];b.asyncLoad=(typeof c.asyncLoad!="undefined")?c.asyncLoad:true;if(b.tipGroups.length>0){b.handleAsyncLoad(function(){b.showNextTipGroup(b.tipGroups[0])})}};a.showNextTipGroup=function(d){var b=this;d.index=b.currentTipGroupIndex;var c=new jive.Tips.TipGroup(d);if(c.hasVisibleTips()){b.addListeners(c);c.showNextTip()}else{if(b.hasUnviewedTipGroups()){b.showNextTipGroup(b.tipGroups[++b.currentTipGroupIndex])}}};a.addListeners=function(c){var b=this;c.addListener("state",function(d){b.emit("state",d)});c.addListener("close",function(d){b.emit("state",d);if(b.hasUnviewedTipGroups()){b.showNextTipGroup(b.tipGroups[++b.currentTipGroupIndex])}})};a.hasUnviewedTipGroups=function(){return this.currentTipGroupIndex<(this.tipGroups.length-1)};a.handleAsyncLoad=function(c){var b=this;if(b.asyncLoad){jive.conc.nextTick(c)}else{c()}}});