function addLabelFocus(){var b=document.getElementById(this.getAttribute("for"));b.focus();if(b.getAttribute("type")=="checkbox"){if(!b.checked){b.checked=true;this.className="checkbox_checked"}else{b.checked=false;this.className="checkbox_unchecked"}}else{if(b.getAttribute("type")=="radio"){var c=document.getElementsByTagName("input");var d=new Array();for(var a=0;a<c.length;a++){if(c[a].getAttribute("name")==b.getAttribute("name")){d.push(c[a])}}for(a=0;a<d.length;a++){if(d[a]["checked"]&&d[a].getAttribute("id")!=b.getAttribute("id")){d[a]["checked"]=false}}b.checked=true}}}function safariCheck(){if((navigator.userAgent.indexOf("Safari")>0)&&(navigator.platform.indexOf("Mac")!=-1)&&(navigator.userAgent.indexOf("Version/")==-1)){var b=document.getElementsByTagName("label");for(var a=0;a<b.length;a++){b[a].addEventListener("click",addLabelFocus,false)}}}var crir={init:function(){var a=document.getElementsByTagName("label");for(var b=0;b<a.length;b++){if(a[b].getAttributeNode("for")&&a[b].getAttributeNode("for").value!=""){var d=a[b].getAttributeNode("for").value;var e=document.getElementById(d)}else{continue}var f=e.className;if(f=="crirHiddenJS"){e.className="crirHidden";var c=e.getAttributeNode("type").value;if(c=="checkbox"){e.onclick=crir.toggleCheckboxLabel}else{e.onclick=crir.toggleRadioLabel}if(e.checked){if(c=="checkbox"){a[b].className="checkbox_checked"}else{a[b].className="radio_checked"}}else{if(c=="checkbox"){a[b].className="checkbox_unchecked"}else{a[b].className="radio_unchecked"}}}else{if(e.nodeName!="SELECT"&&e.getAttributeNode("type").value=="radio"){a[b].onclick=crir.toggleRadioLabel;e.onclick=crir.toggleRadioLabel}}}},findLabel:function(c){var a=document.getElementsByTagName("label");for(var b=0;b<a.length;b++){if(a[b].getAttributeNode("for")&&a[b].getAttributeNode("for").value==c){return a[b]}}},toggleCheckboxLabel:function(){var a=crir.findLabel(this.getAttributeNode("id").value);if(a.className=="checkbox_checked"){a.className="checkbox_unchecked"}else{a.className="checkbox_checked"}},toggleRadioLabel:function(){var a=crir.findLabel(this.getAttributeNode("id").value);var b=this;var g=b.getAttributeNode("name").value;var c=document.getElementsByTagName("input");for(var d=0;d<c.length;d++){var e=c[d].getAttributeNode("type").value;if(e=="radio"){var k=c[d].getAttributeNode("name").value;var j=c[d].className;if(k==g&&j=="crirHidden"){var h=c[d].getAttributeNode("id").value;var f=crir.findLabel(h);f.className="radio_unchecked"}}}if(b.className=="crirHidden"){a.className="radio_checked"}},addEvent:function(c,b,d,a){if(c.addEventListener){c.addEventListener(b,d,a);return true}else{if(c.attachEvent){return c.attachEvent("on"+b,d)}else{c["on"+b]=d}}}};crir.addEvent(window,"load",crir.init,false);crir.addEvent(window,"load",safariCheck,false);