function featureVideo(d,a,e,b,c){VideoActionBean.hasExistingFeaturedVideo(a,e,{callback:function(f){if("false"==f){saveFeaturedVideo(d,a,e,b,c)}else{$j("#modal_replace_featured").lightbox_me({closeSelector:".jive-modal-close, .close",onLoad:function(){try{var i=JSON.parse(f);var g=$j("#featured-vid-to-replace");if(g&&g.length>0){$j("#feat-replace-info")[0].hide();g[0].innerHTML="<a href='"+i.url+"'>"+i.description+"</a>";$j("#feat-replace-prompt")[0].show()}}catch(h){}}})}},errorHandler:function(){saveFeaturedVideo(d,a,e,b,c)}})}function saveFeaturedVideo(d,a,e,b,c){VideoActionBean.featureVideo(d,a,e,{callback:function(){if(c){b.apply(this,c)}else{if(b){b.call()}}},errorHandler:function(f){alert("Error featuring video: "+f)}})}function clearFeaturedVideo(a,d,b,c){VideoActionBean.clearFeaturedVideo(a,d,{callback:function(){if(c){b.apply(this,c)}else{if(b){b.call()}}},errorHandler:function(e){alert("Error clearing featured video: "+e)}})}function videoFeaturedSucces(){$j("#jive-link-feature").hide();$j("#jive-link-unfeature").show();$j("#jive-vid-featured").fadeIn()}function videoClearFeaturedSucces(){$j("#jive-link-feature").show();$j("#jive-link-unfeature").hide();$j("#jive-vid-featured").fadeOut()};