function JiveViewVideos(){this.reload=function(a){$j("#jive-viewvideos-form").ajaxSubmit({success:function(b){$j("#jive-view-videos-container").html(b)}});return false};this.changeView=function(a){$j("#jiveviewvideosform-view").val(a);this.reload(false)};this.resetSort=function(a){$j("#jiveviewvideosform-sort").val(a);this.setStart(0)};this.setStart=function(a){$j("#jiveviewvideosform-start").val(a);this.reload(false)};this.setStartAndScroll=function(a){$j("#jiveviewvideosform-start").val(a);this.reload(true)}}var jiveviewvideos=new JiveViewVideos();