module("jive.oo._abstract",{setup:function(){this.AbstractClass=jive.oo.Class.extend(function(a){this.publicMember=jive.oo._abstract;a.protectedMember=jive.oo._abstract})}});test("An abstract class cannot be instantiated",1,function(){try{var a=new this.AbstractClass()}catch(b){ok(true,"AbstractClass throws an error when you try to instantiate it")}});test("abstract classes can be subclassed",1,function(){try{var b=this.AbstractClass.extend(function(c){this.publicMember=function(){return"foo"};c.protectedMember=function(){return"bar"}})}catch(a){ok(false,"An error occurred creating a subclass of AbstractClass")}ok(typeof b!="undefined","successfully created a subclass of AbstractClass")});test("concrete subclasses can be instantiated",1,function(){try{var c=this.AbstractClass.extend(function(d){this.publicMember=function(){return"foo"};d.protectedMember=function(){return"bar"}});var a=new c()}catch(b){ok(false,"An error occurred creating an instance of ConcreteClass")}ok(a instanceof c,"successfully created an instance of ConcreteClass")});