var hoverElements=new Array();function onhover(a){if(typeof(a)=="undefined"||typeof(a.className)!="undefined"){return}hoverElements[a]=a.className;a.className=a.className+" hilite"}function onhoverbyclass(b,a){hoverElements[b]=b.className;b.className=b.className+" "+a}function offhover(a){if(hoverElements[a]){a.className=hoverElements[a]}}function onclick(a){onhover(a)}function clearhover(a){a.className=a.className.replace(/hilite/g,"")};