var RED_DOT = "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAYAAACNbyblAAAAHElEQVQI12P4//8/w38GIAXDIBKE0DHxgljNBAAO9TXL0Y4OHwAAAABJRU5ErkJggg==";
var LARGE_IMAGE = "data:image/png;base64,";
var TEST_IMG_SRC = CS_RESOURCE_BASE_URL + "/test/image.png";

module("Jive Image Plugin", {
    setup: function(){
        this.plugin = ed.plugins.jiveimage;

        var imageService = this.plugin.rte.getImageService();

        //ImageService scale mock
        when(imageService).scaleClientImage(anything()).then(function(dataUrl){
            var scalePromise = new jive.conc.Promise();
            scalePromise.emitSuccess(dataUrl);
            return scalePromise;
        });

        //ImageService create mock
        when(imageService).create(anything(), anything()).then(function(name, dataUrl){
            var restPromise = new jive.conc.Promise();
            var img = {
                id: 1,
                name: name,
                url: TEST_IMG_SRC
            };
            restPromise.emitSuccess(img);
            return restPromise;
        });
    }
});

function endsWith(str, suffix, message){
    var pos = str.indexOf(suffix);
    push(pos >= 0 && pos + suffix.length == str.length, str, suffix, message);
}

asyncTest("uploadClientImage", 1, function() {
    var dataUrlImage = $j("<img />").attr("src", RED_DOT);
    this.plugin.uploadClientImage("test.png", dataUrlImage.get(0)).addCallback(function(img){
        endsWith(img.src, TEST_IMG_SRC, "image url replaced");
        start();
    });
});

module("ImageService", {
    setup: function(){
        var that = this;

        $j.mockjax({
            url: '/__services/v2/rest/rteImages/settings*',
            responseText: {
            }
        });

        $j.mockjax({
            url: '/__services/v2/rest/rteImages*',
            type: "POST",
            response: function(settings){
                if(that.validate){
                    this.responseText = that.validate(settings) || {};
                }else{
                    this.responseText = {};
                }
            }
        });

        this.imageService = new jive.rte.ImageService({
            objectId: -1,
            objectType: -1,
            containerId: 1,
            containerType: 1

        }); //TODO: fake params to fill defaultParams, so we can test URL construction.

        this.testImg = {
            id: 1,
            name: "redDot.png",
            url: "testResult/redDot.png"
        };
    },
    teardown: function(){
        $j.mockjaxClear();
    }
});

asyncTest("scaleDataUrl with small image", 1, function(){
    this.imageService.scaleClientImage(RED_DOT).addCallback(function(scaled){
        equal(scaled, RED_DOT);
        start();
    });
});

asyncTest("scaleDataUrl with large image", 3, function(){
    this.imageService.scaleClientImage(LARGE_IMAGE).addCallback(function(scaled){
        ok(scaled.length <= 2048 * 1024 * 4/3, "byte length");
        $j("<img />").load(function(){
            ok(this.width <= 1920, "width");
            ok(this.height <= 1080, "height");
            start();
        }).attr("src", scaled);
    })
});

asyncTest("create", function(){
    var that = this;
    this.validate = function(settings){
        equal(settings.url, "/__services/v2/rest/rteImages?objectId=-1&objectType=-1&containerId=1&containerType=1&name=redDot.png", "url check");
        equal(settings.data, RED_DOT, "data check");
        return that.testImg;
    };
    this.imageService.create("redDot.png", RED_DOT).addCallback(function(img){
        deepEqual(img, that.testImg, "return value passthrough");
        start();
    });
});